var fed2html, should;

should = require("chai").should();

fed2html = require("../index");

describe("Fed_2html", function() {
  return it("处理简单EJS模板文件", function(done) {
    var opts;
    opts = fed2html.resolveConfig("./config.json");
    return fed2html.doParse(opts, function(a, b) {
      return done(a);
    });
  });
});
